/// @brief Get memory size string
/// @param size Memory size
/// @param uppercase If true, return uppercase version
/// @return Memory size string
inline constexpr std::string_view get_memory_size_string( MemorySize size, bool uppercase = false ) noexcept {
	auto idx = static_cast<std::size_t>( size );
	if ( idx >= MEMORY_SIZE_STRINGS_LOWER.size() ) {
		return "";
	}
	return uppercase ? MEMORY_SIZE_STRINGS_UPPER[idx] : MEMORY_SIZE_STRINGS_LOWER[idx];
}

