/// @brief Get NASM memory size string (no "ptr" suffix)
/// @param size Memory size
/// @param uppercase If true, return uppercase version
/// @return NASM-style memory size string
inline constexpr std::string_view get_nasm_memory_size_string( MemorySize size, bool uppercase = false ) noexcept {
	auto idx = static_cast<std::size_t>( size );
	if ( idx >= NASM_MEMORY_SIZE_STRINGS_LOWER.size() ) {
		return "";
	}
	return uppercase ? NASM_MEMORY_SIZE_STRINGS_UPPER[idx] : NASM_MEMORY_SIZE_STRINGS_LOWER[idx];
}

