/// @brief Get register name
/// @param index Register index
/// @param uppercase If true, return uppercase version
/// @return Register name
inline constexpr std::string_view get_register_name( uint32_t index, bool uppercase = false ) noexcept {
	if ( index >= REGISTER_NAMES_LOWER.size() ) {
		return "???";
	}
	return uppercase ? REGISTER_NAMES_UPPER[index] : REGISTER_NAMES_LOWER[index];
}

